'                    p_json_code           => NULL,',
'                    p_json_remark         => ''Form to register new employees''',
'                );',
'',
'    -- update session',
'    update SFB_JSON_FORM',
'       set JSON_USER_SESSION_ID = v(''APP_SESSION'')',
'     where JSON_ID = V_JSON_ID',
'    ;',
'',
'    v_json_id_emp_form := V_JSON_ID;',
'',
'    V_HFAT_ID    := null;',
