'DECLARE',
'    v_insert_start    VARCHAR2(4000) := chr(13) ||''update SFB_HELP_TEXT set HETE_TEXT = ( HETE_TEXT||'';  -- Start des Insert-Befehls',
'    v_insert_end      VARCHAR2(4000) := '') where HETE_ID = 1 ;''|| chr(13);                                               -- Ende des Insert-Befehls',
unistr('    v_HETE_TEXT       CLOB;                                                                -- Variable f\00FCr den CLOB-Text'),
