unistr('    v_HETE_TEXT_full  clob;                                                     -- Variable f\00FCr den Teiltext (max. 32k Zeichen)'),
unistr('    v_clob_length     INTEGER;                                                             -- Gesamtl\00E4nge des CLOB-Texts'),
unistr('    v_chunk_size      INTEGER := 4000;                                                    -- Max. Gr\00F6\00DFe f\00FCr jeden Textblock'),
'    v_HETE_ID         NUMBER := :P211_HETE_ID;                                                       -- HETE_ID zur Identifikation',
unistr('    v_num_iterations  INTEGER;                                                             -- Anzahl der ben\00F6tigten Iterationen'),
unistr('    v_position        INTEGER;                                                             -- Variable f\00FCr die Startposition'),
'BEGIN',
'    -- Extrahieren des CLOB-Werts aus der Datenbank basierend auf HETE_ID',
'    SELECT HETE_TEXT',
'      INTO v_HETE_TEXT',
'      FROM SFB_HELP_TEXT',
'    WHERE HETE_ID = v_HETE_ID;',
'',
unistr('    -- Bestimme die Gesamtl\00E4nge des CLOB-Texts'),
'    v_clob_length := DBMS_LOB.GETLENGTH(v_HETE_TEXT);',
'',
'    -- Berechne die Anzahl der Iterationen',
unistr('    v_num_iterations := CEIL(v_clob_length / v_chunk_size);  -- Aufrunden, um alle Bl\00F6cke abzudecken'),
'',
unistr('    -- FOR-Schleife \00FCber die berechnete Anzahl von Iterationen'),
'    FOR i IN 1..v_num_iterations  LOOP',
'        -- Berechne die Startposition des aktuellen Blocks',
