'',
'    END LOOP;',
'    ',
unistr('    -- Update-Funktion (falls ben\00F6tigt)'),
'    UPDATE SFB_HELP_TEXT',
'    SET HETE_TEXT_SQL =''begin'' || chr(13) || ''delete from SFB_HELP_TEXT;',
'',
