'            v_bracket := ''}'';',
'        end if;',
'',
'        ',
'        if v_HRJF_ARRAY_YN = ''Y'' then ',
'',
'            v_array_part_Start  := ''"type": "array", "items": {'';',
'            v_array_part_end    := ''}''; ',
